,p_attribute_03=>'UserAuthentication'
,p_attribute_05=>'N'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'function UserAuthentication ( p_username IN VARCHAR2, ',
' p_password IN VARCHAR2 ',
') return boolean',
'is',
'v varchar2(1) ;',
'BEGIN',
'select ''a'' into v from users u, CLIENT_INFORMATION c ',
'WHERE u.client_id=c.client_id and user_id=:P101_USERNAME',
'AND password =DBMS_OBFUSCATION_TOOLKIT.md5 (input => UTL_RAW.cast_to_raw(:P101_PASSWORD))',
'AND u.active=''1'' and c.active=''1'' ;',
'RETURN TRUE ;',
'EXCEPTION',
'WHEN NO_DATA_FOUND THEN ',
'RETURN FALSE ;',
'END ;'))
,p_invalid_session_type=>'LOGIN'
,p_use_secure_cookie_yn=>'N'
,p_ras_mode=>0
);
end;
